			function insertQuestion($question, $type, $qNum){
				$extraQPos = "SELECT * FROM Question_ET_Pos";
				$extraQText = "SELECT * FROM Question_ExtraText";
				$extraPos = mysqli_fetch_assoc(mysqli_query($GLOBALS['connection'], $extraQPos));
				$extraText = mysqli_fetch_assoc(mysqli_query($GLOBALS['connection'], $extraQText));
				
				
				
				
				
				
				echo "<h3>" . $qNum . ": " . $question . "</h3>";
				
				if($extraPos["Q" . $qNum] === '1'){
					echo "<p class = 'extraText'>" . $extraText["Q" . $qNum] . "</p>";
				}
				
				
				
				
				//Decides what kind of question to make
				if($type === "Short"){
					echo "<input type = 'text' size = '25' name ='Q" . $qNum . "'><br>";
				}else if($type === "Long"){
					echo "<textarea class='special' name = 'Q" . $qNum . "'></textarea><br>";
				}else if($type === "Num"){
					echo "<input type = 'number' size = '25' name ='Q" . $qNum . "'><br>";
				}else if($type === "MC"){
					$format = "<section class = 'section'>";
					
					//Selections Setup
					$sql = "SELECT * FROM Selections WHERE selection_num = " . $qNum;
					$selectResult = mysqli_query($GLOBALS['connection'], $sql);
					$row = mysqli_fetch_assoc($selectResult);
					$begin = 1;
					
					//Sets up The selection menu of a question
					for($x = 0; $x < 10; $x++){
						if(!empty($row['selection_' . $begin]) && $begin !== 10){
							if($begin == 1){
								$format = $format . "<input type = 'radio' class = 'mc' value = '" . $row['selection_' . $begin] . "' name = 'Q" . $qNum . "'>" . $row['selection_' . $begin] . "<br>";
							}else{
								$format = $format . "<input type = 'radio' class = 'mc' value = '" . $row['selection_' . $begin] . "' name = 'Q" . $qNum . "'>" . $row['selection_' . $begin] . "<br>";
							}
						}
						if($begin === 10){
							$format = $format . "</section>";
						}
						$begin++;
					}
					
				}else if($type === "Select"){
					$format = "<section class = 'section'><select name = 'Q" . $qNum . "' onChange = 'display()'>";
					
					//Selections Setup
					$sql = "SELECT * FROM Selections WHERE selection_num = " . $qNum;
					$selectResult = mysqli_query($GLOBALS['connection'], $sql);
					$row = mysqli_fetch_assoc($selectResult);
					$begin = 1;
					
					//Sets up The selection menu of a question
					for($x = 0; $x < 10; $x++){
						if(!empty($row['selection_' . $begin]) && $begin !== 10){
							if($begin == 1){
								$format = $format . "<option class = 'selection' value = 'selection_" . $begin . "' selected>" . $row['selection_' . $begin] . "</option>";	
							}else{
								$format = $format . "<option class = 'selection' value = 'selection_" . $begin . "'>" . $row['selection_' . $begin] . "</option>";	
							}
						}
						if($begin === 10){
							$format = $format . "<option value = 'other' class = 'other'>Other...</select></section>";
						}
						$begin++;
					}
					
				
				}
				

				
				
				
				echo $format;

				if($extraPos["Q" . $qNum] === '2'){
					echo "<p class = 'extraText'>" . $extraText["Q" . $qNum] . "</p>";
				}
				
			}
		
		
		
		
		
			$host = "";
