			$questionLength = count($questionsRow);
			
			$sqlQuestionType = "SELECT * FROM Question_Type";
			$types = mysqli_query($connect, $sqlQuestionType);
			$typesRow =  mysqli_fetch_assoc($types);
			
			$application_Info = "SELECT * FROM Application_Info";
			$appInfo = mysqli_fetch_assoc(mysqli_query($connect, $application_Info));
			
			$start = 1;
			
			echo "<h2>" . $appInfo['Title'] . "</h2>";
			
			//Inserts all Questions
			for($x = 0; $x < $questionLength; $x++){
				$qType = $typesRow["Q" . $start];
				$question = $questionsRow["Q" . $start];
				insertQuestion($question, $qType, $start);
				$start++;
